CREATE DATABASE petDatabase;USE petDatabase;CREATE TABLE petPet (    petname VARCHAR(20) PRIMARY KEY,    owner VARCHAR(45),    species ENUM('M', 'F'),    gender VARCHAR(15),    birth DATE,    death DATE);CREATE TABLE petEvent (    petname VARCHAR(20) PRIMARY KEY,    eventdate DATE,    eventtype VARCHAR(20),    remark VARCHAR(255),    FOREIGN KEY (petname) REFERENCES petPet(petname));